<?php namespace App\Http\Controllers\Admin\Auth;use App\Http\Controllers\Controller;use Illuminate\Http\Request;use Illuminate\Support\Facades\Auth;class LoginController extends Controller{public function index(){return view("admin.auth.login");}public function login(Request $request){$email=$request->email;$pass=$request->password;if(Auth::guard("admin")->attempt(["email"=>$email,"password"=>$pass],true)){return redirect("/".adminPrefix()."/home");}else{$request->session()->flash("auth_error_message",'خطأ في البريد الإلكتروني أو كلمة المرور');return back();}}}